$(document).ready(function () {
    
    var $aboutdrawer = $("#aboutsubmenu"); // Cache your elements
  var $abouttogg   = $("#about");

  $aboutdrawer.hide();
  $abouttogg.show();

  $abouttogg.hover(function(){
     $aboutdrawer.finish().slideToggle();
  }); 
    
});  
    

// Sticky Navbar function
$(document).ready(function () {

window.onscroll = function() {myFunction()};

// Get the navbar
var navbar = document.getElementById("arrow");
var scrollnav = document.getElementById("scrollheader")
// Get the offset position of the navbar
var switchpoint = navbar.offsetTop;
    
// Add the sticky class to the navbar when you reach its scroll position. Remove "sticky" when you leave the scroll position
function myFunction() {
  if (window.pageYOffset >= switchpoint) {
    scrollnav.classList.add("visible");
  } else {
    scrollnav.classList.remove("visible");
  }
}
    });  


//Scroll down arrow
$(document).ready(function () {
$('#arrow').click(function(){
    $("html, body").animate({ scrollTop: $('#main').offset().top}, 1000);
    return false;
 });
        }); 